(79, 'Error while sending blast email to  users ( V 3.1)', NULL, 'The issue came with distribution  2.9 , 3.0 and 3.1 . \r\n\r\nFollow patch the code step\r\n\r\n1. Open file /protected/app/controllers/ConfigController.php \r\n2. Find function postBlast() ;\r\n3. Change current code with code bellow \r\n\r\n<pre>\r\nfunction postDoblast()\r\n{\r\n\r\n	$rules = array(\r\n		''subject''		=> ''required'',\r\n		''message''		=> ''required|min:10'',\r\n		''groups''		=> ''required'',				\r\n	);	\r\n	$validator = Validator::make(Input::all(), $rules);	\r\n	if ($validator->passes()) \r\n	{	\r\n\r\n		if(!is_null(Input::get(''groups'')))\r\n		{\r\n			$groups = Input::get(''groups'');\r\n			for($i=0; $i<count($groups); $i++)\r\n			{\r\n				if(Input::get(''uStatus'') == ''all'')\r\n				{\r\n					$users = DB::table(''tb_users'')->where(''group_id'',$groups[$i])->get();\r\n				} else {\r\n					$users = DB::table(''tb_users'')->where(''active'',Input::get(''uStatus''))->where(''group_id'',$groups[$i])->get();\r\n				}\r\n				$count = 0;\r\n				foreach($users as $row)\r\n				{\r\n\r\n					$to = $row->email;\r\n					$subject = Input::get(''subject'');\r\n					$message = Input::get(''message'');\r\n					$headers  = ''MIME-Version: 1.0'' . "\\r\\n";\r\n					$headers .= ''Content-type: text/html; charset=iso-8859-1'' . "\\r\\n";\r\n					$headers .= ''From: ''.CNF_APPNAME.'' <''.CNF_EMAIL.''>'' . "\\r\\n";\r\n						mail($to, $subject, $message, $headers);\r\n					\r\n					$count = ++$count;					\r\n				} \r\n				\r\n			}\r\n			return Redirect::to(''config/blast'')->with(''messagetext'',''Total ''.$count.'' Message has been sent'')->with(''msgstatus'',''success'');\r\n\r\n		}\r\n		return Redirect::to(''config/blast'')->with(''messagetext'',''No Message has been sent'')->with(''msgstatus'',''info'');\r\n		\r\n\r\n	} else {\r\n\r\n		return Redirect::to(''config/blast'')->with(''messagetext'', ''The following errors occurred'')->with(''msgstatus'',''error'')\r\n		->withErrors($validator)->withInput();\r\n	}	\r\n\r\n}\r\n</pre>\r\n\r\n ', 13, '2015-05-24 23:56:11', 1, 1, '0'),
